// Extract and validate webhook data
const body = $input.first().json.body || $input.first().json;

const userId = body.user_id;
const message = body.message;
const accessToken = body.access_token;
const timestamp = body.timestamp || Date.now();

if (!userId || !message || !accessToken) {
  throw new Error('Missing required fields: user_id, message, or access_token');
}

// Memory key based on user
const memoryKey = `line_chat_${userId}`;

// Get existing conversation from workflow static data
let conversation = [];
try {
  const staticData = $getWorkflowStaticData('global');
  conversation = staticData[memoryKey] || [];
} catch (e) {
  conversation = [];
}

// Add user message to conversation
conversation.push({
  role: 'user',
  content: message,
  timestamp: timestamp
});

// Keep only last 20 messages to avoid context overflow
if (conversation.length > 20) {
  conversation = conversation.slice(-20);
}

return {
  userId,
  message,
  accessToken,
  memoryKey,
  conversation,
  timestamp
};

// Process AnythingLLM response
const response = $input.first().json;
const prevData = $('Parse Request & Memory').first().json;

let textResponse = '';
let imageBase64 = null;
let imageType = null;

// Extract text response
if (response.textResponse) {
  textResponse = response.textResponse;
} else if (response.response) {
  textResponse = response.response;
} else if (response.message) {
  textResponse = typeof response.message === 'string' ? response.message : response.message.content || '';
}

// Check if response contains image data (base64)
const imageMatch = textResponse.match(/\[IMAGE:(data:image\/(\w+);base64,([^\]]+))\]/);
if (imageMatch) {
  imageType = imageMatch[2]; // png, jpg, etc.
  imageBase64 = imageMatch[3];
  textResponse = textResponse.replace(imageMatch[0], '').trim();
}

// Also check for markdown image with base64
const mdImageMatch = textResponse.match(/!\[.*?\]\(data:image\/(\w+);base64,([^)]+)\)/);
if (mdImageMatch) {
  imageType = mdImageMatch[1];
  imageBase64 = mdImageMatch[2];
  textResponse = textResponse.replace(mdImageMatch[0], '').trim();
}

// Update conversation memory
let conversation = prevData.conversation || [];
conversation.push({
  role: 'assistant',
  content: textResponse || '[Image sent]',
  timestamp: Date.now()
});

// Save to workflow static data
try {
  const staticData = $getWorkflowStaticData('global');
  staticData[prevData.memoryKey] = conversation;
} catch (e) {
  console.log('Failed to save memory:', e);
}

return {
  ...prevData,
  textResponse: textResponse || null,
  hasImage: !!imageBase64,
  imageBase64,
  imageType,
  conversation
};
